  paytime, fare, paytype
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, 
  #{paytime,jdbcType=TIMESTAMP}, #{fare,jdbcType=DECIMAL}, #{paytype,jdbcType=CHAR}
  )

mapper.update:

update orderpay
set "state" = #{state,jdbcType=INTEGER},
  paytime = #{paytime,jdbcType=TIMESTAMP},
  fare = #{fare,jdbcType=DECIMAL},
  paytype = #{paytype,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}
  and userid = #{userid,jdbcType=INTEGER}

mapper.select:

select id, userid, "state", paytime, fare, paytype
from orderpay
where id = #{id,jdbcType=INTEGER}
  and userid = #{userid,jdbcType=INTEGER}
